package com.scjp.chapter4.Examples;

public class ArithmeticOperations {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int a = 10;
       int b = 20;
       int c = 30;
       int d = 40;
       
       
       System.out.println(a);
       System.out.println(b);
       System.out.println(c);
       System.out.println(d);
       System.out.println("addition of a and b = " + (a+b));
       System.out.println("addition of a,b,c = " + (a+b+c));
       System.out.println("addition of a,b,c and d = " + (a+b+c+d));
       System.out.println("subtraction of a,b =" + (a-b));
       System.out.println("subtraction of a,b,c = " +(a-b-c));
       System.out.println("subtraction of a,b,c,d = " + (a-b-c-d));
       System.out.println("multiplication of a and b = " +(a*b));
       System.out.println("multiplication of a,b and c = " + (a*b*c));
       System.out.println("multiplication of a,b,c,d = "+ (a*b*c*d));
       System.out.println("division of a and b = " + (a/b));
       System.out.println("division of c and d = " + (c/d));
       System.out.println("c%a =" + (c%a));
       System.out.println("a++ =" + a++);
       System.out.println("d-- =" + d--);
       System.out.println("b++ =" + b++);
       System.out.println("++b =" + ++b);
       System.out.println("c-- =" + c++);
       System.out.println("--c =" + c--);
       
       
	}

}
